try {
  // A saída da IA é uma string JSON, então primeiro convertemos para um objeto
  const agentOutput = JSON.parse($('AI Agent').first().json.output);
  // Retornamos o resumo para ser usado nos próximos nós
  return { summary: agentOutput.summary };
} catch (error) {
  // Se houver erro (improvável), retorna uma mensagem padrão
  return { summary: 'O cliente pediu para falar com um humano, mas o resumo falhou.' };
}
